        this.headers = new HttpHeaders().set('Content-Type', 'application/json');
        this.idPlan = '';
    }
    //Get informations of Plans
    async getInformationPlans() {
        return new Promise(resolve => {
            this.http.get(this.apiUrl + '/plataformas').toPromise().then(data => {
                resolve(data);
            }).catch(er => {
                resolve(er);
            });
        });
    }
    //Get informations of Specific Plans
    async getInformationSpecificPlans() {
        return new Promise(resolve => {
            this.http.get(this.apiUrl + '/planos/' + this.idPlan).toPromise().then(data => {
                resolve(data);
            }).catch(er => {
                resolve(er);
            });
        });
    }
}
